import React, { Component } from "react";
import M from "materialize-css";
import "materialize-css/dist/css/materialize.min.css";
import "./Slider.css";

class Slider extends Component {
  componentDidMount() {
    const height = "100vh";
    const options = {
      indicators: true,
      height: height,
      interval: 4000,
      duration: 500,
    };
    M.Slider.init(this.Slider, options);
  }

  render() {
    return (
      <div className="body">
        <div
          ref={(Slider) => {
            this.Slider = Slider;
          }}
          className="slider"
        >
          {/* If you want fullscreen slider then add fullscreen to
      this div */}
          <ul className="slides">
            <li>
              <img src="/img/background1.png" />
              <div className="caption center-align">
                <div className="center">
                  <div className="space">
                    <div className="firstleft">
                        <div className="firstleftspace">
                            <span className="gnghouse">GNG HOUSE</span>
                            <div className="firstleftbox">
                                <span className="공간연출">공간연출</span>
                                <span>GNG는 Give aNd Give의 줄임말로서 한 식구처럼 지내고, 사회적 공헌을 할 수 있는 업무를 목표로 하는 회사를 나타냅니다.<br/><br/>

                                </span>
                            </div>
                        </div>
                    </div>
                    <div className="firstright">기모띠</div>
                  </div>
                </div>
              </div>
            </li>
            {/* <li>
              <img src="/img/background1.png" />
              <div className="caption center-align">
                <h3>Left Aligned Caption</h3>
                <h5 className="light grey-text text-lighten-3">
                  Here's our small slogan.
                </h5>
              </div>
            </li> */}
            {/* <li>
              <img src="/img/background1.png" />
              <div className="caption center-align">
                <h3>Right Aligned Caption</h3>
                <h5 className="light grey-text text-lighten-3">
                  Here's our small slogan.
                </h5>
              </div>
            </li> */}
            {/* <li>
              <img src="/img/background1.png" />
              <div className="caption center-align">
                <h3>This is our big Tagline!</h3>
                <h5 className="light grey-text text-lighten-3">
                  Here's our small slogan.
                </h5>
              </div>
            </li> */}
          </ul>
        </div>
      </div>
    );
  }
}

export default Slider;
